package java_fundamentals;
import java.util.*;
public class sum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a=sc.nextInt();
		int b=sc.nextInt();
		int c=a+b;
		System.out.println("The sum of "+ a +" and "+ b + " is 10" + c );
		sc.close();

	}

}
